mapper.select:
     	select ${attr1}, ${attr2}, count(*) as count
		from jysg 
		 where YEAR(sgfssj) >= ${starty} and YEAR(sgfssj) <= ${endy}   
		
			and lh = ${set}
		
		
		
			and ${attr1} is null
		
		
			and ${attr1} = ${content1}
			
		
			and ${attr2} is null
		
		
			and ${attr2} = ${content2}
		
		group by ${attr1}, ${attr2}

     	select ${attr1}, ${attr2}, count(*) as count
     	from jysg
     	 where YEAR(sgfssj) >= ${starty} and YEAR(sgfssj) <= ${endy} 
     	
			and lh = ${set}
		
		
		
			and ${attr1} in
			
               ${item}
            
		
		
		
			and ${attr2} in
			
               ${item}
            
		
		group by ${attr1}, ${attr2}
		order by ${attr1}, ${attr2}

     	select ${attr}, count(*) as count
     	from jysg
     	 where YEAR(sgfssj) >= ${starty} and YEAR(sgfssj) <= ${endy}   
     	
			and lh = ${set}
		
		group by ${attr}
