mapper.select:
select YEAR(sgfssj) as year , count(*) as count from ybsg1 where sgfssj is not null
and lh = #{set}
group by year having year >= #{starty} and year <= #{endy} order by year

select YEAR(sgfssj) as year , MONTH(sgfssj) as month, count(*) as count from ybsg1 where sgfssj is not null
and lh = #{set}
group by year,month having year >= #{starty} and year <= #{endy} order by year

select YEAR(sgfssj) as year , xq as week, count(*) as count from ybsg1 where sgfssj is not null and xq is not null
and lh = #{set}
group by year,xq having year >= #{starty} and year <= #{endy} order by year

select YEAR(sgfssj) as year, week(sgfssj,1) as week2, count(*) as count from ybsg1 where sgfssj is not null
and lh = #{set}
group by year, week2 having year >= #{starty} and year <= #{endy} and week2 > 0 order by year

select YEAR(sgfssj) as year , QUARTER(sgfssj) as quarter, count(*) as count from ybsg1 where sgfssj is not null and xq is not null
and lh = #{set}
group by year,QUARTER(sgfssj) having year >= #{starty} and year <= #{endy} order by year

     	select DAY(sgfssj) as day, count(*) as count
     	from ybsg1
     	where sgfssj is not null and YEAR(sgfssj) = #{year}

			and MONTH(sgfssj) = #{month}

     	group by day
     	order by day

     	select DATE(sgfssj) as date, count(*) as count
     	from ybsg1
     	where sgfssj is not null and YEAR(sgfssj) = #{year}

			and MONTH(sgfssj) = #{month}

     	group by date
     	order by date
